"use client"

import { useState } from "react"
import "./CreatePollScreen.css"

const CreatePollScreen = ({ createPoll, isLoading }) => {
  const [question, setQuestion] = useState("")
  const [options, setOptions] = useState(["", ""])
  const [correctAnswer, setCorrectAnswer] = useState(0)
  const [timeLimit, setTimeLimit] = useState(60)

  const addOption = () => {
    if (options.length < 6) {
      setOptions([...options, ""])
    }
  }

  const updateOption = (index, value) => {
    const newOptions = [...options]
    newOptions[index] = value
    setOptions(newOptions)
  }

  const handleSubmit = () => {
    const validOptions = options.filter((opt) => opt.trim())
    if (validOptions.length < 2) return

    const pollData = {
      question: question.trim(),
      options: validOptions,
      correctAnswer,
      timeLimit,
    }
    createPoll(pollData)
  }

  return (
    <div className="create-poll-container">
      <div className="create-poll-card fade-in">
        <div className="interview-poll-badge">Intervue Poll</div>
        <div className="create-poll-header">
          <h1 className="create-poll-title">Let's Get Started</h1>
          <p className="create-poll-subtitle">
            You'll have the ability to create and manage polls, ask questions, and monitor your students' responses in
            real-time.
          </p>
        </div>

        <div className="form-group">
          <label className="form-label">Enter your question</label>
          <input
            className="form-input"
            type="text"
            placeholder="Enter your question"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
          />
          <div className="character-count">0/100</div>
        </div>

        <div className="options-section">
          <label className="form-label">Edit Options</label>

          {options.map((option, index) => (
            <div key={index} className="option-input-group">
              <div className="option-number">{String.fromCharCode(65 + index)}</div>
              <input
                className="form-input option-input"
                type="text"
                placeholder="Enter option"
                value={option}
                onChange={(e) => updateOption(index, e.target.value)}
              />
              <div className="correct-answer-group">
                <div
                  className={`correct-answer-radio ${correctAnswer === index ? "selected" : ""}`}
                  onClick={() => setCorrectAnswer(index)}
                />
                <span className="correct-label">{correctAnswer === index ? "Yes" : "No"}</span>
              </div>
            </div>
          ))}

          <button className="add-option-btn" onClick={addOption} disabled={options.length >= 6}>
            + Add More option
          </button>
        </div>

        <button
          className="btn-primary"
          onClick={handleSubmit}
          disabled={!question.trim() || options.filter((opt) => opt.trim()).length < 2 || isLoading}
        >
          {isLoading ? "Creating..." : "Ask Question"}
        </button>
      </div>
    </div>
  )
}

export default CreatePollScreen
